Rules.InvariantScript:
UpdateContext({ varShowAddItemsDialog: false });

// should go last
Set(
    gblPreviousScreen,
    Self
);

Navigate(
    'Item Consumption Product Search Screen',
    ScreenTransition.Cover,
    {WorkOrderJob: WorkOrderJob}
);
UpdateContext({varShowAddItemsDialog: false});

Navigate(
    'Item Consumption Product Search Screen',
    ScreenTransition.Cover,
    {
        WorkOrderJob: WorkOrderJob,
        SearchText: Self.Value
    }
);
UpdateContext({varShowAddItemsDialog: false});
