Rule.InvariantScript:
With(
        {
            controlHeights: [
                conItemDetails.Height,
                conInventoryDimensions.Height,
                conTrackingDimensions.Height
            ]
        },
        Sum(
            controlHeights,
            Value
        ) + Self.PaddingTop + Self.PaddingBottom + Self.LayoutGap * CountRows(controlHeights)
    )

Rules.InvariantScript:
Max(
    Parent.Height - Self.Y - conActionsWOJICES.Height,
    With(
        {
            controlHeights: [
                conItemDetails.Height,
                conInventoryDimensions.Height,
                conTrackingDimensions.Height
            ]
        },
        Sum(
            controlHeights,
            Value
        ) + Self.PaddingTop + Self.PaddingBottom + Self.LayoutGap * CountRows(controlHeights)
    )
)

With(
    {
        controlHeights: [
            cmpProductNumber.Height,
            cmpProductName.Height
        ]
    },
    Sum(
        controlHeights,
        Value
    ) + Self.PaddingTop + Self.PaddingBottom + Self.LayoutGap * CountRows(controlHeights)
)

With(
    {
        controlHeights: [
            lblEditableTextHeader.Height,
            lblEditableTextValue.Height
        ]
    },
    Sum(
        controlHeights,
        Value
    ) + Self.PaddingTop + Self.PaddingBottom + Self.LayoutGap * CountRows(controlHeights)
)

With(
